! STVINT                                           
      SUBROUTINE STVINT(HH,WW)                                                 
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)   
      DIMENSION HH(28),WW(28),MINIMO(7),MAXIMO(7)  
      COMMON/STV/XINTT,YINTT,ZINTT,TAA,X0X0,Y0Y0,Z0Z0,                  &
                 XIXI,YIYI,ZIZI,XJXJ,YJYJ,ZJZJ,NINI,NJNJ      
      DATA MINIMO /1,2,4,7,11,16,22/                                       
      DATA MAXIMO /1,3,6,10,15,21,28/                                      
!-----------------------------------------------------------------------
!     Gauss-Hermite Quadrature
!-----------------------------------------------------------------------
      XINTT = 0.0D0                                                       
      YINTT = 0.0D0                                                       
      ZINTT = 0.0D0                                                       
      NPTS = (NINI+NJNJ-2)/2+1                                              
      IMIN = MINIMO(NPTS)                                                  
      IMAX = MAXIMO(NPTS)                                                  
      DO I = IMIN,IMAX                                              
       DUM = WW(I)                                                     
       PX = DUM                                                       
       PY = DUM                                                       
       PZ = DUM                                                       
       DUM = HH(I)*TAA                                                   
       IF(NINI>1) THEN                                               
        AX = DUM+X0X0-XIXI                                              
        AY = DUM+Y0Y0-YIYI                                              
        AZ = DUM+Z0Z0-ZIZI                                              
        GO TO (16,15,14,13,12,11,10),NINI                         
   10   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
   11   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
   12   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
   13   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
   14   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
   15   PX = PX*AX                                                  
        PY = PY*AY                                                  
        PZ = PZ*AZ                                                  
       END IF                                                         
   16  CONTINUE                                                       
       IF(NJNJ>1) THEN                                               
        BX = DUM+X0X0-XJXJ                                              
        BY = DUM+Y0Y0-YJYJ                                              
        BZ = DUM+Z0Z0-ZJZJ                                              
        GO TO (27,26,25,24,23,22,21,20),NJNJ                  
   20   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   21   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   22   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   23   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   24   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   25   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
   26   PX = PX*BX                                               
        PY = PY*BY                                               
        PZ = PZ*BZ                                               
       END IF                                                         
   27  CONTINUE                                                       
       XINTT = XINTT + PX                                           
       YINTT = YINTT + PY                                           
       ZINTT = ZINTT + PZ                                           
      END DO
!-----------------------------------------------------------------------
      RETURN                                                            
      END                                                               

! SETCONI
      SUBROUTINE SETCONI(CONI,IPP,CS,CP,CD,CF,CG,CH,CI,NPRIMI)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)   
      DIMENSION CONI(*),KARTEN(0:6)
      DOUBLE PRECISION,DIMENSION(NPRIMI) :: CS,CP,CD,CF,CG,CH,CI
      DATA KARTEN/1,3,6,10,15,21,28/
!-----------------------------------------------------------------------
      CONI(1) = CS(IPP)
      CALL DACOPY(KARTEN(1),CP(IPP),CONI(2),1)
      CALL DACOPY(KARTEN(2),CD(IPP),CONI(5),1)
      CALL DACOPY(KARTEN(3),CF(IPP),CONI(11),1)
      CALL DACOPY(KARTEN(4),CG(IPP),CONI(21),1)
      CALL DACOPY(KARTEN(5),CH(IPP),CONI(36),1)
      CALL DACOPY(KARTEN(6),CI(IPP),CONI(57),1)
!-----------------------------------------------------------------------
      RETURN
      END
            
! DACOPY                                           
      SUBROUTINE DACOPY(N,DA,DX,INCX)                                   
      IMPLICIT DOUBLE PRECISION(A-H,O-Z)                                
      DIMENSION DX(*)                                                   
!-----------------------------------------------------------------------
!     INITIALISES A VECTOR WITH A CONSTANT: DX(I) <== DA                                                
!-----------------------------------------------------------------------
      IF(N<=0)RETURN                                                  
      IF(INCX/=1)THEN
       IX = 1                                                            
       IF(INCX<0)IX=(-N+1)*INCX + 1                                 
       DO I = 1,N                                                     
        DX(IX) = DA                                                     
        IX = IX + INCX                                                  
       END DO
       RETURN                                                            
      END IF
      M = MOD(N,7)                                                      
      IF(M/=0)THEN
       DO I = 1,M                                                     
        DX(I)=DA                                                        
       END DO
       IF(N<7)RETURN
      END IF                                             
      MP1 = M + 1                                                       
      DO I = MP1,N,7                                                 
       DX(I)=DA                                                        
       DX(I + 1)=DA                                                    
       DX(I + 2)=DA                                                    
       DX(I + 3)=DA                                                    
       DX(I + 4)=DA                                                    
       DX(I + 5)=DA                                                    
       DX(I + 6)=DA                                                    
      END DO
!-----------------------------------------------------------------------
      RETURN                                                            
      END                                                               

