!-----------------------------------------------------------------------                                                                       
!     PREPARE SHELL INFORMATION/FOR HONDO INTEGRATION 
      NORM = .TRUE.  
      IF(NELEC==2) GO TO 200                                          
!                                                                       
!     ----- PERMUTE ISH AND JSH SHELLS, FOR THEIR TYPE                  
!     THIS IS DONE FOR SPEED REASONS.  THE CODE GETS THE RIGHT ANSWER   
!     WITHOUT THE ANGULAR MOMENTUM FLIPPING, AND THEREFORE A CALLING    
!     ARGUMENT ALLOWS ONE DO EXACTLY THE integral BLOCK AS SPECIFIED,   
!     SHOULD THAT BE DESIRED.                                           
!                                                                       
      IANDJ = ISH == JSH                                              
      IF (KTYPE(ISH) < KTYPE(JSH)  .and.  FLIP) THEN                 
       INU = JSH                                                      
       JNU = ISH                                                      
       NGTI = NGTH(2)                                                 
       NGTJ = NGTH(1)                                                 
      ELSE                                                              
       INU = ISH                                                      
       JNU = JSH                                                      
       NGTI = NGTH(1)                                                 
       NGTJ = NGTH(2)                                                 
      END IF                                                            
!                                                                       
!     ----- ISHELL                                                      
!                                                                       
      I = KATOM(INU)                                                    
      AX = Cxyz(1,I)                                                       
      AY = Cxyz(2,I)                                                       
      AZ = Cxyz(3,I)                                                       
      I1 = KSTART(INU)                                                  
      I2 = I1+KNG(INU)-1                                                
      LIT = KTYPE(INU)                                                  
      MINI = KMIN(INU)                                                  
      MAXI = KMAX(INU)                                                  
      LOCI = KLOC(INU)-MINI                                             
      NGA = 0                                                           
      DO I = I1,I2                                                  
       NGA = NGA+1                                                    
       GA(NGA)  = EX(I)                                                
       CSA(NGA) = CS(I)                                               
       CPA(NGA) = CP(I)                                               
       CDA(NGA) = CD(I)                                               
       CFA(NGA) = CF(I)                                               
       CGA(NGA) = CG(I)                                               
       CHA(NGA) = CH(I)                                               
       CIA(NGA) = CI(I)                                               
      END DO                                                          
!                                                                       
!     ----- JSHELL                                                      
!                                                                       
      J = KATOM(JNU)                                                    
      BX = Cxyz(1,J)                                                       
      BY = Cxyz(2,J)                                                       
      BZ = Cxyz(3,J)                                                       
      J1 = KSTART(JNU)                                                  
      J2 = J1+KNG(JNU)-1                                                
      LJT = KTYPE(JNU)                                                  
      MINJ = KMIN(JNU)                                                  
      MAXJ = KMAX(JNU)                                                  
      LOCJ = KLOC(JNU)-MINJ                                             
      NGB = 0                                                           
      DO J = J1,J2                                                  
       NGB = NGB+1                                                    
       GB(NGB) = EX(J)                                                
       CSB(NGB) = CS(J)                                               
       CPB(NGB) = CP(J)                                               
       CDB(NGB) = CD(J)                                               
       CFB(NGB) = CF(J)                                               
       CGB(NGB) = CG(J)                                               
       CHB(NGB) = CH(J)                                               
       CIB(NGB) = CI(J)                                               
      END DO                                                          
      RAB = ((AX-BX)*(AX-BX) + (AY-BY)*(AY-BY) + (AZ-BZ)*(AZ-BZ))       
!                                                                       
!     ----- PREPARE INDICES FOR PAIRS OF (I,J) FUNCTIONS                
!                                                                       
      IJ = 0                                                            
      JMAX = MAXJ                                                       
      DO I = MINI,MAXI                                              
       NX = IX(I)                                                     
       NY = IY(I)                                                     
       NZ = IZ(I)                                                     
       IF (IANDJ) JMAX = I                                            
       DO J = MINJ,JMAX                                           
        IJ = IJ+1                                                   
        IJX(IJ) = NX+JX(J)                                          
        IJY(IJ) = NY+JY(J)                                          
        IJZ(IJ) = NZ+JZ(J)                                          
        IJGT(IJ) = NGTI*(I-MINI)+NGTJ*(J-MINJ)+1                    
       END DO                                                       
      END DO                                                          
      RETURN                                                            
!     ******                                                            
!                                                                       
!        K AND L SHELL                                                  
!                                                                       
  200 CONTINUE                                                          
      KANDL = KSH == LSH                                              
      SAME = ISH == KSH .and. JSH == LSH                            
!                                                                       
!     ----- PERMUTE KSH AND LSH SHELLS, FOR THEIR TYPE                  
!                                                                       
      IF (KTYPE(KSH) < KTYPE(LSH)  .and.  FLIP) THEN                 
       KNU = LSH                                                      
       LNU = KSH                                                      
       NGTK = NGTH(4)                                                 
       NGTL = NGTH(3)                                                 
      ELSE                                                              
       KNU = KSH                                                      
       LNU = LSH                                                      
       NGTK = NGTH(3)                                                 
       NGTL = NGTH(4)                                                 
      END IF                                                            
!                                                                       
!     ----- K SHELL                                                     
!                                                                       
      K = KATOM(KNU)                                                    
      CX = Cxyz(1,K)                                                       
      CY = Cxyz(2,K)                                                       
      CZ = Cxyz(3,K)                                                       
      K1 = KSTART(KNU)                                                  
      K2 = K1+KNG(KNU)-1                                                
      LKT = KTYPE(KNU)                                                  
      MINK = KMIN(KNU)                                                  
      MAXK = KMAX(KNU)                                                  
      LOCK = KLOC(KNU)-MINK                                             
      NGC = 0                                                           
      DO K = K1,K2                                                  
       NGC = NGC+1                                                    
       GC(NGC)  = EX(K)                                                
       CSC(NGC) = CS(K)                                               
       CPC(NGC) = CP(K)                                               
       CDC(NGC) = CD(K)                                               
       CFC(NGC) = CF(K)                                               
       CGC(NGC) = CG(K)                                               
       CHC(NGC) = CH(K)                                               
       CIC(NGC) = CI(K)                                               
      END DO                                                          
!                                                                       
!     ----- LSHELL                                                      
!                                                                       
      L = KATOM(LNU)                                                    
      DX = Cxyz(1,L)                                                       
      DY = Cxyz(2,L)                                                       
      DZ = Cxyz(3,L)                                                       
      L1 = KSTART(LNU)                                                  
      L2 = L1+KNG(LNU)-1                                                
      LLT = KTYPE(LNU)                                                  
      MINL = KMIN(LNU)                                                  
      MAXL = KMAX(LNU)                                                  
      LOCL = KLOC(LNU)-MINL                                             
      NGD = 0                                                           
      DO L = L1,L2                                                  
       NGD = NGD+1                                                    
       GD(NGD) = EX(L)                                                
       CSD(NGD) = CS(L)                                               
       CPD(NGD) = CP(L)                                               
       CDD(NGD) = CD(L)                                               
       CFD(NGD) = CF(L)                                               
       CGD(NGD) = CG(L)                                               
       CHD(NGD) = CH(L)                                               
       CID(NGD) = CI(L)                                               
      END DO                                                          
      NROOTS = (LIT+LJT+LKT+LLT-2)/2                                    
      RCD = ((CX-DX)*(CX-DX) + (CY-DY)*(CY-DY) + (CZ-DZ)*(CZ-DZ))       
!                                                                       
!     ----- PREPARE INDICES FOR PAIRS OF (K,L) FUNCTIONS                
!                                                                       
      KL = 0                                                            
      LMAX = MAXL                                                       
      DO K = MINK,MAXK                                              
       NX = KX(K)                                                     
       NY = KY(K)                                                     
       NZ = KZ(K)                                                     
       IF (KANDL) LMAX = K                                            
       DO L = MINL,LMAX                                           
        KL = KL+1                                                   
        KLX(KL) = NX+LX(L)                                          
        KLY(KL) = NY+LY(L)                                          
        KLZ(KL) = NZ+LZ(L)                                          
        KLGT(KL) = NGTK*(K-MINK)+NGTL*(L-MINL)                      
       END DO                                                       
      END DO                                                          
      MAX = KL                                                          
      DO 320 I = 1,IJ                                                   
      IF (SAME) MAX = I                                                 
  320 IK(I) = MAX                                                       
      IJKL = IJ*KL                                                      
      IF (SAME) IJKL = IJ*(IJ+1)/2                                      
      RETURN                                                            
      END                                                               

! IJPRIM                                           
      SUBROUTINE IJPRIM(DDIJ)                                           
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
