  276          IF (NORM) DUM1 = DUM1*SQRT3                              
               GO TO 280                                                
  277          IF (NORM) DUM1 = DUM1*SQRT7/(SQRT5*SQRT3)                
               GO TO 280                                                
  278          IF (NORM) DUM1 = DUM1*SQRT5                              
