  379             IF (NORM) DUM2 = DUM2*SQRT5/SQRT3                     
!                                                                       
  380             NN = NN+1                                             
                  DDIJ(NN) = DUM2                                       
  400          CONTINUE                                                 
  420       CONTINUE                                                    
            IF ( .NOT. IANDJ) GO TO 520                                 
            IF (IA == JB) GO TO 520                                   
