! DAWERF
      DOUBLE PRECISION FUNCTION DAWERF(Y)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      COMMON /DERFCM/ C(246),IFRST(40),ILAST(40),H
!     EVALUATES THE HYBRID DAWSON-ERROR FUNCTION
      PARAMETER (ZP5=0.5D0, TEN=10.0D0)
      X= ABS(Y)
      IF(X>=TEN) GO TO 115

      XN = X/H
      NX = INT(XN)+1
      I1 = IFRST(NX)
      IL = ILAST(NX)
      T= C(IL)
      DO 110 K=IL-1,I1,-1
  110 T= C(K)+T*X
      DAWERF= T
      RETURN
  115 CONTINUE
      X2I = 1.0d0/(X*X)
      IL = 8
      TXT=(IL +ZP5)*X2I
      SUM= TXT
      DO 120 K=IL,1,-1
         TXT= TXT-X2I
  120 SUM= TXT*(1.0d0+SUM)
      DAWERF= X*TXT*(1.0d0+SUM)
      RETURN
      END
      
! DAWF
      DOUBLE PRECISION FUNCTION DAWF(Y)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      COMMON /DAWFCM/ C(249),IFRST(40),ILAST(40),H
!     EVALUATES THE DAWSON FUNCTION -----
      PARAMETER (ZP5=0.5D0, TEN=10.0D0)
!
      X= Y
      S= 1.0d0
      IF(X>=0.0d0) GO TO 105
      X=-Y
      S=-1.0d0
  105 IF(X>=TEN) GO TO 115
      XN = X/H
      NX = INT(XN)+1
      I1 = IFRST(NX)
      IL = ILAST(NX)
      T= C(IL)
      DO 110 K=IL-1,I1,-1
  110 T= C(K)+T*X
      DAWF  = T*S
      RETURN
!
  115 CONTINUE
      X2I = 1.0d0/(X*X)
      IL = 8
      TXT=(IL +ZP5)*X2I
      SUM= TXT
      DO 120 K=IL,1,-1
         TXT= TXT-X2I
  120 SUM= TXT*(1.0d0+SUM)
      DAWF = Y*TXT*(1.0d0+SUM)
      RETURN
      END
      
