!-----------------------------------------------------------------------
      if(IERITYP==1 .or. IERITYP==3)then      ! ERITYP = FULL or MIX
       DO I=1,NSHELL
        IATOM = KATOM(I)
!lib        CALL updatebasis(BASLIB,I,Cxyz(1:3,IATOM))
