                147,  49,  98,  49,  98, 147, 147,  98/                  
      DATA IZ /   1,   1,   1, 344,   1,   1, 687,   1, 344, 344,       &
                  1,   1,1030,   1, 344,   1, 344, 687, 687, 344,       &
                  1,   1,1373,   1, 344,   1, 344,1030,1030,   1,       &
