                  1,   1,2059,   1, 344,   1, 344,1716,1716,   1,       &
                687,   1, 687,1373,1373, 344, 344,1373,   1,1030,       &
               1030, 344, 687, 344, 687,1030,1030, 687/                 
!-----------------------------------------------------------------------                                                                       
!     PREPARE SHELL INFORMATION/FOR HONDO INTEGRATION                   
      IF(NELEC==2) GO TO 200      
      NORM = .TRUE.  
!                                                                       
!     ----- PERMUTE ISH AND JSH SHELLS, FOR THEIR TYPE                  
!     THIS IS DONE FOR SPEED REASONS.  THE CODE GETS THE RIGHT ANSWER   
!     WITHOUT THE ANGULAR MOMENTUM FLIPPING, AND THEREFORE A CALLING    
!     ARGUMENT ALLOWS ONE DO EXACTLY THE integral BLOCK AS SPECIFIED,   
!     SHOULD THAT BE DESIRED.                                           
!                                                                       
      IANDJ = ISH == JSH                                              
      IF (KTYPE(ISH) < KTYPE(JSH)  .and.  FLIP) THEN                 
       INU = JSH                                                      
       JNU = ISH                                                      
       NGTI = NGTH(2)                                                 
       NGTJ = NGTH(1)                                                 
      ELSE                                                              
       INU = ISH                                                      
       JNU = JSH                                                      
       NGTI = NGTH(1)                                                 
       NGTJ = NGTH(2)                                                 
      END IF                                                            
!                                                                       
!     ----- ISHELL                                                      
!                                                                       
      I = KATOM(INU)                                                    
      AX = Cxyz(1,I)                                                       
      AY = Cxyz(2,I)                                                       
      AZ = Cxyz(3,I)                                                       
      I1 = KSTART(INU)                                                  
      I2 = I1+KNG(INU)-1                                                
      LIT = KTYPE(INU)                                                  
      MINI = KMIN(INU)                                                  
      MAXI = KMAX(INU)                                                  
      LOCI = KLOC(INU)-MINI                                             
      NGA = 0                                                           
      DO I = I1,I2                                                  
       NGA = NGA+1                                                    
       GA(NGA) = EX(I)                                                
       CSA(NGA) = CS(I)                                               
       CPA(NGA) = CP(I)                                               
       CDA(NGA) = CD(I)                                               
       CFA(NGA) = CF(I)                                               
       CGA(NGA) = CG(I)                                               
       CHA(NGA) = CH(I)                                               
       CIA(NGA) = CI(I)                                               
      END DO                                                          
!                                                                       
!     ----- JSHELL                                                      
!                                                                       
      J = KATOM(JNU)                                                    
      BX = Cxyz(1,J)                                                       
      BY = Cxyz(2,J)                                                       
      BZ = Cxyz(3,J)                                                       
      J1 = KSTART(JNU)                                                  
      J2 = J1+KNG(JNU)-1                                                
      LJT = KTYPE(JNU)                                                  
      MINJ = KMIN(JNU)                                                  
      MAXJ = KMAX(JNU)                                                  
      LOCJ = KLOC(JNU)-MINJ                                             
      NGB = 0                                                           
      DO J = J1,J2                                                  
       NGB = NGB+1                                                    
       GB(NGB) = EX(J)                                                
       CSB(NGB) = CS(J)                                               
       CPB(NGB) = CP(J)                                               
       CDB(NGB) = CD(J)                                               
       CFB(NGB) = CF(J)                                               
       CGB(NGB) = CG(J)                                               
       CHB(NGB) = CH(J)                                               
       CIB(NGB) = CI(J)                                               
      END DO                                                          
      RAB = ((AX-BX)*(AX-BX) + (AY-BY)*(AY-BY) + (AZ-BZ)*(AZ-BZ))       
!                                                                       
!     ----- PREPARE INDICES FOR PAIRS OF (I,J) FUNCTIONS                
!                                                                       
      IJ = 0                                                            
      JMAX = MAXJ                                                       
      DO I = MINI,MAXI                                              
       NX = IX(I)                                                     
       NY = IY(I)                                                     
       NZ = IZ(I)                                                     
       IF (IANDJ) JMAX = I                                            
       DO J = MINJ,JMAX                                           
        IJ = IJ+1                                                   
        IJX(IJ) = NX+JX(J)                                          
        IJY(IJ) = NY+JY(J)                                          
        IJZ(IJ) = NZ+JZ(J)                                          
        IJGT(IJ) = NGTI*(I-MINI)+NGTJ*(J-MINJ)+1                    
       END DO                                                   
      END DO                                                         
      RETURN                                                            
!     ******                                                            
!                                                                       
!        K AND L SHELL                                                  
!                                                                       
  200 CONTINUE    
      NORM  = .FALSE.      
      KANDL = .FALSE.                                 
      SAME  = .FALSE.          

      KNU = KSH                                                      
      LNU = 1                                                      
      NGTK = NGTH(3)                                                 
      NGTL = NGTH(4)                                                 
!                                                                       
!     ----- K SHELL                                                     
!                                                                       
      K = KATOMaux(KNU)                                                 
      CX = Cxyz(1,K)                                                       
      CY = Cxyz(2,K)                                                       
      CZ = Cxyz(3,K)                                                       
      K1 = KNU                                                 
      K2 = KNU                                               
      LKT = KTYPEaux(KNU)                                               
      MINK = MINF(LKT)                                                  
      MAXK = MAXF(LKT)                                                  
      LOCK = KLOCaux(KNU)-MINK                                          
      NGC = 0                                                           
      DO K = K1,K2                                                  
       NGC = NGC+1                                                    
       GC(NGC) = EXaux(K)                                             
       CSC(NGC) = Caux(K)                                            
       CPC(NGC) = Caux(K)                                         
       CDC(NGC) = Caux(K)                                             
       CFC(NGC) = Caux(K)                                             
       CGC(NGC) = Caux(K)                                             
       CHC(NGC) = Caux(K)                                             
       CIC(NGC) = Caux(K)                                             
      END DO                                                          
!                                                                       
!     ----- LSHELL (Unity Shell)                                                     
!                                                                       
      L = 1                                                    
      DX = 0                                                       
      DY = 0                                                       
      DZ = 0                                                       
      L1 = 1                                                
      L2 = 1                                                
      LLT = 1                                                  
      MINL = 1                                                  
      MAXL = 1                                                  
      LOCL = 1                                             
      NGD = 0                                                           
      DO L = L1,L2                                                  
       NGD = NGD+1                                                    
       GD(NGD) = 0                                                
       CSD(NGD) = 1                                             
       CPD(NGD) = 1                                              
       CDD(NGD) = 1                                               
       CFD(NGD) = 1                                               
       CGD(NGD) = 1                                               
       CHD(NGD) = 1                                               
       CID(NGD) = 1                                               
      END DO                                                          
      NROOTS = (LIT+LJT+LKT+LLT-2)/2                                    
      RCD = ((CX-DX)*(CX-DX) + (CY-DY)*(CY-DY) + (CZ-DZ)*(CZ-DZ))       
!                                                                       
!     ----- PREPARE INDICES FOR PAIRS OF (K,L) FUNCTIONS                
!                                                                       
      KL = 0                                                            
      LMAX = MAXL                                                       
      DO K = MINK,MAXK                                              
       NX = KX(K)                                                     
       NY = KY(K)                                                     
       NZ = KZ(K)                                                     
       IF (KANDL) LMAX = K                                            
       DO L = MINL,LMAX                                           
        KL = KL+1                                                   
        KLX(KL) = NX+LX(L)                                          
        KLY(KL) = NY+LY(L)                                          
        KLZ(KL) = NZ+LZ(L)                                          
        KLGT(KL) = NGTK*(K-MINK)+NGTL*(L-MINL)                      
       END DO                                                       
      END DO                                                          
      MAX = KL                                                          
      DO 320 I = 1,IJ                                                   
      IF (SAME) MAX = I                                                 
  320 IK(I) = MAX                                                       
      IJKL = IJ*KL                                                      
      IF (SAME) IJKL = IJ*(IJ+1)/2

      RETURN                                                            
      END       

! PDPT_msqrt
      SUBROUTINE PDPT_msqrt(GMAT,N,IPRINTOPT)
      INTEGER :: N,I,J
      DOUBLE PRECISION :: GMAT(N,N),TOL,VEC(N,N),EIG(N),W(N),AUX(N,N)
!
      TOL = 1.0d-10
      AUX = GMAT
      CALL DIAG(N,AUX,VEC,EIG,W)
!
      AUX = 0.0d0
      DO I=1,N
       IF (EIG(I)<=TOL) THEN
        IF(IPRINTOPT==1) write(6,*) "Truncating",I,EIG(I)
        EIG(I) = 0.0d0
        DO J=1,N
         VEC(J,I) = 0.0d0
        END DO
       ELSE
        AUX(I,I) = 1.0/DSQRT(EIG(I))
       END IF
      END DO

      GMAT = MATMUL(MATMUL(VEC,AUX),TRANSPOSE(VEC))

      END

! QOUT2C                                             
      SUBROUTINE QOUT2C(GMAT,NBFaux,GHONDO,MAXG)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)                               
      LOGICAL IANDJ,KANDL,SAME
      COMMON/MISC  /IANDJ,KANDL,SAME                                  
      COMMON/ERIOUT/ISH,JSH,KSH,LSH,LSTRI,LSTRJ,LSTRK,LSTRL           
      COMMON/SHLNOS/LIT,LJT,LKT,LLT,LOCI,LOCJ,LOCK,LOCL,                &
                    MINI,MINJ,MINK,MINL,MAXI,MAXJ,MAXK,MAXL,            &
                    NIJ,IJ,KL                                          
      COMMON/NSHELaux/KATOMaux(500),KTYPEaux(500),KLOCaux(500)
!
      DOUBLE PRECISION,DIMENSION(NBFaux,NBFaux) :: GMAT
      DOUBLE PRECISION,DIMENSION(MAXG) :: GHONDO
      INTEGER,DIMENSION(8) :: MINF,MAXF
      DATA MINF / 1, 2,  5, 11, 21, 36, 57, 1/                 
      DATA MAXF / 1, 4, 10, 20, 35, 56, 84, 4/                 
!-----------------------------------------------------------------------                                                                       
!     Get Auxiliary Basis Info. 
!-----------------------------------------------------------------------
      SAME  = ISH == KSH                            
      MINI = MINF(KTYPEaux(ISH))                                        
      MINK = MINF(KTYPEaux(KSH))                                        
      MAXI = MAXF(KTYPEaux(ISH))                                        
      MAXK = MAXF(KTYPEaux(KSH))                                        
      LOCI = KLOCaux(ISH)-MINI                                         
      LOCK = KLOCaux(KSH)-MINK                                          
!-----------------------------------------------------------------------                                                                       
!     Store (k|l) ERIs in G matrix 
!-----------------------------------------------------------------------
      DO 1 I = MINI,MAXI                                              
        I_INDEX = (I-MINI)*LSTRI + 1                                   
        DO K =  MINK,MAXK                                       
          IK_INDEX = (K-MINK)*LSTRK + I_INDEX                    
          IF(SAME.and.K>I)GO TO 1                 
          VAL = GHONDO( IK_INDEX )
          I1 = LOCI+I                                           
          I3 = LOCK+K                                           
          GMAT(I1,I3) = VAL
          GMAT(I3,I1) = VAL
        END DO
    1 CONTINUE                                                       
!-----------------------------------------------------------------------                                                                       
      RETURN                                                            
      END                                                               

! QOUT3C                                     
      SUBROUTINE QOUT3C(BUFP2,GHONDO,MAXG,GMAT,NBF,NBFaux,              &
                        KLOC,KMIN,KMAX,NSHELL)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)                               
      LOGICAL IANDJ,KANDL,SAME
      COMMON/MISC/IANDJ,KANDL,SAME                                  
      COMMON/ERIOUT/ISH,JSH,KSH,LSH,LSTRI,LSTRJ,LSTRK,LSTRL           
      COMMON/NSHELaux/KATOMaux(500),KTYPEaux(500),KLOCaux(500)
!
      INTEGER,DIMENSION(NSHELL) :: KLOC,KMIN,KMAX
      DOUBLE PRECISION,DIMENSION(NBF*(NBF+1)/2,NBFaux) :: BUFP2
      DOUBLE PRECISION,DIMENSION(NBFaux,NBFaux)::GMAT
      DOUBLE PRECISION,DIMENSION(MAXG)::GHONDO
      INTEGER,DIMENSION(8) :: MINF,MAXF
      DATA MINF / 1, 2,  5, 11, 21, 36, 57, 1/                 
      DATA MAXF / 1, 4, 10, 20, 35, 56, 84, 4/      
      INTEGER :: T
!-----------------------------------------------------------------------                                                                       
!     Get Basis and Auxiliary Basis Info. 
!-----------------------------------------------------------------------
      IANDJ = ISH == JSH                                              
      MINI = KMIN(ISH)                                                  
      MINJ = KMIN(JSH)                                                  
      MINK = MINF(KTYPEaux(KSH))                                        
      MAXI = KMAX(ISH)                                                  
      MAXJ = KMAX(JSH)                                                  
      MAXK = MAXF(KTYPEaux(KSH))                                        
      LOCI = KLOC(ISH)-MINI                                             
      LOCJ = KLOC(JSH)-MINJ                                             
      LOCK = KLOCaux(KSH)-MINK                                          
!-----------------------------------------------------------------------                                                                       
!     Build B tensor (Only Upper Triangular)
!      b_mn^l = sum_k (mn|k) G^{-1/2}_{kl}
!-----------------------------------------------------------------------
      JMAX = MAXJ                                                       
      DO I = MINI,MAXI                                              
       I_INDEX = (I-MINI)*LSTRI + 1                                   
       IF (IANDJ) JMAX = I                                            
       DO J = MINJ,JMAX                                           
        IJ_INDEX = (J-MINJ)*LSTRJ + I_INDEX                         
        DO K = MINK,MAXK                                       
          IJK_INDEX = (K-MINK)*LSTRK + IJ_INDEX                    
          VAL = GHONDO( IJK_INDEX ) 
          M = LOCI+I                                           
          N = LOCJ+J                                           
          KK = LOCK+K                                           
          IF (M <= N) GO TO 100                             
          T = M                                                
          M = N                                               
          N = T                                                
  100     MN = M + N*(N-1)/2
          DO L=1,NBFaux
           BUFP2(MN,L) = BUFP2(MN,L) + VAL*GMAT(KK,L)
          END DO
        END DO
       END DO                                                       
      END DO
!-----------------------------------------------------------------------                                                                       
      RETURN 
      END                                                               

! QOUT3CModChol
      SUBROUTINE QOUT3CModChol(BUFP2,GHONDO,MAXG,GMAT,NBF,NBFaux,       &
                               KLOC,KMIN,KMAX,NSHELL)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      LOGICAL IANDJ,KANDL,SAME
      COMMON/MISC/IANDJ,KANDL,SAME
      COMMON/ERIOUT/ISH,JSH,KSH,LSH,LSTRI,LSTRJ,LSTRK,LSTRL
      COMMON/NSHELaux/KATOMaux(500),KTYPEaux(500),KLOCaux(500)
!
      INTEGER,DIMENSION(NSHELL) :: KLOC,KMIN,KMAX
      DOUBLE PRECISION,DIMENSION(NBF*(NBF+1)/2,NBFaux) :: BUFP2
      DOUBLE PRECISION,DIMENSION(NBFaux,NBFaux)::GMAT
      DOUBLE PRECISION,DIMENSION(MAXG)::GHONDO
      INTEGER,DIMENSION(8) :: MINF,MAXF
      DATA MINF / 1, 2,  5, 11, 21, 36, 57, 1/
      DATA MAXF / 1, 4, 10, 20, 35, 56, 84, 4/
!-----------------------------------------------------------------------                                                                       
!     Get Basis and Auxiliary Basis Info. 
!-----------------------------------------------------------------------
      IANDJ = ISH == JSH
      MINI = KMIN(ISH)
      MINJ = KMIN(JSH)
      MINK = MINF(KTYPEaux(KSH))
      MAXI = KMAX(ISH)
      MAXJ = KMAX(JSH)
      MAXK = MAXF(KTYPEaux(KSH))
      LOCI = KLOC(ISH)-MINI
      LOCJ = KLOC(JSH)-MINJ
      LOCK = KLOCaux(KSH)-MINK
!-----------------------------------------------------------------------                                                                       
!     Store (mn|P) (Only Upper Triangular)
!-----------------------------------------------------------------------
      JMAX = MAXJ
      DO I = MINI,MAXI
       I_INDEX = (I-MINI)*LSTRI + 1
       IF (IANDJ) JMAX = I
       DO 1 J = MINJ,JMAX
        IJ_INDEX = (J-MINJ)*LSTRJ + I_INDEX
        DO K =  MINK,MAXK
          IJK_INDEX = (K-MINK)*LSTRK + IJ_INDEX
          VAL = GHONDO( IJK_INDEX )
          M = LOCI+I
          N = LOCJ+J
          KK = LOCK+K
          IF (M <= N) GO TO 100
          T = M
          M = N
          N = T
  100     MN = M + N*(N-1)/2
          BUFP2(MN,KK) = VAL
  180     CONTINUE
        END DO
    1  CONTINUE
      END DO
!-----------------------------------------------------------------------                                                                       
      RETURN
      END

! QOUTaux
      SUBROUTINE QOUTaux(BUFP2,GLIBRETA,NBF,NBFaux,ISH,JSH,KLOC,NSHELL)
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      INTEGER,DIMENSION(NSHELL) :: KLOC
      DIMENSION GLIBRETA(*)
      DOUBLE PRECISION,DIMENSION(NBF*(NBF+1)/2,NBFaux) :: BUFP2
      INTEGER :: ORII,ORIJ,ORIK
      COMMON/ORI/ORII,ORIJ,ORIK
      INTEGER :: ISH,JSH
      LOGICAL IANDJ
!-----------------------------------------------------------------------
!     Pack 4-indices into 1 word.
!     Write Label & integral on Unit 1 if DONTW = .False.
!-----------------------------------------------------------------------
      IANDJ = ISH == JSH
      LOCI = KLOC(ISH)
      LOCJ = KLOC(JSH)
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      IJN = 0
      JMAX = ORIJ
      DO I = 1,ORII
       I_INDEX = (I-1)*ORIJ*NBFaux
       IF (IANDJ) JMAX = I
       DO J = 1,JMAX
        IJ_INDEX = (J-1)*NBFaux + I_INDEX
        IJN = IJN+1
        KLN = 0
        DO K = 1,NBFaux
         IJK_INDEX = (K-1) + IJ_INDEX + 1
         VAL = GLIBRETA( IJK_INDEX )
         I1 = LOCI+I-1
         I2 = LOCJ+J-1
         I3 = K
         IF (I1 <= I2) GO TO 100
         N = I1
         I1 = I2
         I2 = N
  100    CONTINUE
         MN = I1 + I2*(I2-1)/2
         BUFP2(MN,K) = VAL
        END DO
       END DO
      END DO
!-----------------------------------------------------------------------
      RETURN
      END

!----------------------------------------------------------------------!
!                                                                      !
!                    Electrostatic Moment Integrals                    !     
!                                                                      !
!----------------------------------------------------------------------!

! INTMOM                                           
      SUBROUTINE INTMOM(LIT1,LJT1,IJ,IJX,IJY,IJZ,DIJ,WINT,AA,AX,AY,AZ)    
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)   
      COMMON/ELPROP/IEMOM      
      COMMON/PRPINT/XINT0,XINT1,XINT2,XINT3,YINT0,YINT1,YINT2,YINT3,    &
                    ZINT0,ZINT1,ZINT2,ZINT3
      COMMON/HERMIT/H1(55),W1(55)
      COMMON/XYZORB/TXYZ,X00,Y00,Z00,XI,YI,ZI,XJ,YJ,ZJ,NI,NJ      
      DIMENSION DIJ(*),WINT(*),IJX(*),IJY(*),IJZ(*)                     
      DIMENSION XIN(49,4),YIN(49,4),ZIN(49,4)                           
!-----------------------------------------------------------------------
!                    Electrostatic Moment Integrals 
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      TXYZ  = 1.0d0/SQRT(AA)                                                 
      X00 = AX                                                           
      Y00 = AY                                                           
      Z00 = AZ                                                           
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      IN = -7                                                           
      DO I=1,LIT1                                                   
       IN = IN+7                                                         
       NI = I                                                            
       DO J=1,LJT1                                                   
        JN = IN+J                                                         
        NJ = J                                                            
        CALL EMOMINT(H1,W1)                                                       
        XIN(JN,1) = XINT0*TXYZ                                               
        YIN(JN,1) = YINT0*TXYZ                                               
        ZIN(JN,1) = ZINT0*TXYZ                                               
        XIN(JN,2) = XINT1*TXYZ                                               
        YIN(JN,2) = YINT1*TXYZ                                               
        ZIN(JN,2) = ZINT1*TXYZ                                               
        IF(IEMOM>=2) THEN                                                  
         XIN(JN,3) = XINT2*TXYZ                                            
         YIN(JN,3) = YINT2*TXYZ                                            
         ZIN(JN,3) = ZINT2*TXYZ                                            
        END IF                                                            
        IF(IEMOM>=3) THEN                                                  
         XIN(JN,4) = XINT3*TXYZ                                            
         YIN(JN,4) = YINT3*TXYZ                                            
         ZIN(JN,4) = ZINT3*TXYZ                                            
        END IF                                                            
       END DO
      END DO
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      DO I=1,IJ                                                     
       NIX = IJX(I)                                                       
       NIY = IJY(I)                                                       
       NIZ = IJZ(I)                                                       
       DENS = DIJ(I)                                                      
       XIN0 = XIN(NIX,1)                                                  
       YIN0 = YIN(NIY,1)                                                  
       ZIN0 = ZIN(NIZ,1)                                                  
       XIN1 = XIN(NIX,2)                                                   
       YIN1 = YIN(NIY,2)                                                
       ZIN1 = ZIN(NIZ,2)                                                
       INDEX = I                                                       
       WINT(INDEX) = WINT(INDEX) + XIN1 * YIN0 * ZIN0 * DENS              
       INDEX = INDEX + IJ                                                
       WINT(INDEX) = WINT(INDEX) + XIN0 * YIN1 * ZIN0 * DENS             
       INDEX = INDEX + IJ                                                
       WINT(INDEX) = WINT(INDEX) + XIN0 * YIN0 * ZIN1 * DENS             
       IF(IEMOM>=2) THEN                                                  
        XIN2 = XIN(NIX,3)                                        
        YIN2 = YIN(NIY,3)                                                
        ZIN2 = ZIN(NIZ,3)                                                
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN2 * YIN0 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN2 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN0 * ZIN2 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN1 * YIN1 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN1 * YIN0 * ZIN1 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN1 * ZIN1 * DENS           
       END IF                                                            
       IF(IEMOM>=3) THEN                                                  
        XIN3 = XIN(NIX,4)                                                
        YIN3 = YIN(NIY,4)                                                
        ZIN3 = ZIN(NIZ,4)                                                
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN3 * YIN0 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN3 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN0 * ZIN3 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN2 * YIN1 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN2 * YIN0 * ZIN1 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN1 * YIN2 * ZIN0 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN2 * ZIN1 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN1 * YIN0 * ZIN2 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN0 * YIN1 * ZIN2 * DENS           
        INDEX = INDEX + IJ                                              
        WINT(INDEX) = WINT(INDEX) + XIN1 * YIN1 * ZIN1 * DENS           
       END IF                                                            
      END DO
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      RETURN                                                            
      END                                                               

! EMOMINT                                           
      SUBROUTINE EMOMINT(H,WW)                                                 
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)   
      COMMON/ELPROP/IEMOM      
      COMMON/PRPINT/XINT0,XINT1,XINT2,XINT3,YINT0,YINT1,YINT2,YINT3,    &
                    ZINT0,ZINT1,ZINT2,ZINT3                              
      COMMON/XYZORB/TXYZ,X00,Y00,Z00,XI,YI,ZI,XJ,YJ,ZJ,NI,NJ             
      COMMON/CMCoord/Xcm,Ycm,Zcm                           
      DIMENSION H(36),WW(36),MINARRAY(8),MAXARRAY(8)                               
      DATA MINARRAY /1,2,4, 7,11,16,22,29/                                   
      DATA MAXARRAY /1,3,6,10,15,21,28,36/
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -                                                                       
!     Gauss-Hermite Quadrature
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      XINT0 = 0.0D+00                                                      
      YINT0 = 0.0D+00                                                      
      ZINT0 = 0.0D+00                                                      
      XINT1 = 0.0D+00                                                      
      YINT1 = 0.0D+00                                                      
      ZINT1 = 0.0D+00                                                      
      XINT2 = 0.0D+00                                                      
      YINT2 = 0.0D+00                                                      
      ZINT2 = 0.0D+00                                                      
      XINT3 = 0.0D+00                                                      
      YINT3 = 0.0D+00                                                      
      ZINT3 = 0.0D+00                                                      
      NPTS = (NI + NJ + IEMOM - 2)/2 + 1                                   
      IMIN = MINARRAY(NPTS)                                                  
      IMAX = MAXARRAY(NPTS)                                                  
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      DO I = IMIN,IMAX                                              
       DUM = WW(I)                                                     
       PX = DUM                                                       
       PY = DUM                                                       
       PZ = DUM
       DUM = H(I)*TXYZ                                                   
       PTX = DUM + X00                                                 
       PTY = DUM + Y00                                                 
       PTZ = DUM + Z00                                                 
       AX = PTX - XI                                                  
       AY = PTY - YI                                                  
       AZ = PTZ - ZI                                                  
       BX = PTX - XJ                                                  
       BY = PTY - YJ                                                  
       BZ = PTZ - ZJ                                                  
       CX = PTX - Xcm                                                  
       CY = PTY - Ycm                                                  
       CZ = PTZ - Zcm                                                  
!                                                                     
       GO TO (101,102,103,104,105,106,107), NI                        
  107  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  106  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  105  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  104  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  103  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  102  PX = PX*AX                                                     
       PY = PY*AY                                                     
       PZ = PZ*AZ                                                     
  101  CONTINUE                                                       
!                                                                       
       GO TO (201,202,203,204,205,206,207), NJ                        
  207  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  206  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  205  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  204  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  203  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  202  PX = PX*BX                                                     
       PY = PY*BY                                                     
       PZ = PZ*BZ                                                     
  201  CONTINUE                                                       
!                                                                     
       IEMOM1 = IEMOM + 1
       GO TO (301,302,303,304),IEMOM1                                    
  304  XINT3 = XINT3 + PX*CX*CX*CX                                    
       YINT3 = YINT3 + PY*CY*CY*CY                                    
       ZINT3 = ZINT3 + PZ*CZ*CZ*CZ                                    
  303  XINT2 = XINT2 + PX*CX*CX                                       
       YINT2 = YINT2 + PY*CY*CY                                       
       ZINT2 = ZINT2 + PZ*CZ*CZ                                       
  302  XINT1 = XINT1 + PX*CX                                          
       YINT1 = YINT1 + PY*CY                                          
       ZINT1 = ZINT1 + PZ*CZ                                          
  301  XINT0 = XINT0 + PX                                             
       YINT0 = YINT0 + PY                                             
       ZINT0 = ZINT0 + PZ                                             
      END DO
!- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
      RETURN                                                            
      END                                                               

!======================================================================!
!                                                                      !
