!-----------------------------------------------------------------------
!     PRODCWQWj1 = CW12j*QWj, j<NB (NSOC is excluded from the Sum)
!     Note: Term with I=J is not included
!-----------------------------------------------------------------------
      PRODCWQWj1 = 0.0d0
      DO I=1,J-1
       PRODCWQWj1 = PRODCWQWj1 + CW12(J,I)*QW(I+J*(J-1)/2)
      ENDDO
      DO I=J+1,NB
       PRODCWQWj1 = PRODCWQWj1 + CW12(J,I)*QW(J+I*(I-1)/2)
      ENDDO
      DO I=NA+1,NBF5
