            IF(IJEQKL.AND.K.EQ.I) LMAX=J
            DO 2610 L=MINL,LMAX
              LS=SPL.AND.L.EQ.1
!
              IF(LS) THEN
                 IF(IJKS) THEN
                    DO IGR=1,NG*NR
                      SL(IGR)=DKLSL(IGR)*SK(IGR)
                    ENDDO
                 ELSE
                    DO IGR=1,NG*NR
                      SL(IGR)=DKLSL(IGR)
                    ENDDO
                 ENDIF
              ELSE
