              IF(IJKLS) THEN
                 DO IGR=1,NG*NR
                   XY(IGR)=XYZ(IGR,NX)*XYZ(IGR,NY)*SL(IGR)
                   XZ(IGR)=XYZ(IGR,NX)*XYZ(IGR,NZ)*SL(IGR)
                   YZ(IGR)=XYZ(IGR,NY)*XYZ(IGR,NZ)*SL(IGR)
                 ENDDO
              ELSE
                 DO IGR=1,NG*NR
                   XY(IGR)=XYZ(IGR,NX)*XYZ(IGR,NY)
