              IIFINT=IIFINT+3
              FD(10)=FD(10)+DAB(NN)*DUMFX
              FD(11)=FD(11)+DAB(NN)*DUMFY
              FD(12)=FD(12)+DAB(NN)*DUMFZ
 2600         CONTINUE
!
 2610       CONTINUE
 2620     CONTINUE
 2630   CONTINUE
 2640 CONTINUE
!
!     ----- ZEROTH AND FIRST DERIVATIVE INTEGRALS -----
!
!     REMOVE NEXT RETURN FOR STORING EACH INTEGRAL CONTRIBUTION
      RETURN
!
      IJKLN=0
      DO 3640 I=MINI,MAXI
        IS=SPI.AND.I.EQ.1
!
        JMAX=MAXJ
        IF(IIEQJJ) JMAX=I
        DO 3630 J=MINJ,JMAX
          JS=SPJ.AND.J.EQ.1
!
          IF(JS) THEN
             IF(IS) THEN
                DO IGR=1,NG*NR
                  SJ(IGR)=DIJSJ(IGR)*DIJSI(IGR)
                ENDDO
             ELSE
                DO IGR=1,NG*NR
                  SJ(IGR)=DIJSJ(IGR)
                ENDDO
             ENDIF
          ELSE
             IF(IS) THEN
                DO IGR=1,NG*NR
                  SJ(IGR)=DIJSI(IGR)
                ENDDO
