          IF(IJEQKL) KMAX=I
          DO 3620 K=MINK,KMAX
            KS=SPK.AND.K.EQ.1
!
            IF(KS) THEN
               IF(IJS) THEN
                  DO IGR=1,NG*NR
                    SK(IGR)=DKLSK(IGR)*SJ(IGR)
                  ENDDO
               ELSE
                  DO IGR=1,NG*NR
                    SK(IGR)=DKLSK(IGR)
                  ENDDO
